// Game answer handler
const guess = parseInt(message.text);
const secretNumber = await User.getData('game_secret_number');
let attempts = await User.getData('game_attempts') || 0;

if (isNaN(guess)) {
    return bot.sendMessage('Please enter a valid number!');
}

attempts++;
await User.saveData('game_attempts', attempts);

if (guess === secretNumber) {
    bot.sendMessage(`ğŸ‰ Congratulations! You guessed the number ${secretNumber} in ${attempts} attempts!`);
    await User.deleteData('game_secret_number');
    await User.deleteData('game_attempts');
} else if (guess < secretNumber) {
    bot.sendMessage('ğŸ“ˆ Too low! Try a higher number.');
} else {
    bot.sendMessage('ğŸ“‰ Too high! Try a lower number.');
}
