// Currency converter template
try {
    const fromCurrency = 'USD';
    const toCurrency = 'EUR';
    const amount = 100;

    const response = await HTTP.get(`https://api.exchangerate-api.com/v4/latest/${fromCurrency}`);
    const rates = JSON.parse(response);

    if (rates.rates && rates.rates[toCurrency]) {
        const rate = rates.rates[toCurrency];
        const converted = (amount * rate).toFixed(2);

