answerHandler:
if (User.getData('asking_name') === 'true') {
    const userName = params;

    if (userName.length < 2) {
        Api.sendMessage("Please enter a valid name (at least 2 characters):");
        return;
    }

    User.saveData('user_name', userName);
    User.saveData('asking_name', 'false');

    Api.sendMessage(`âœ… Nice to meet you, ${userName}! I'll remember your name.`);
}
