// Base64 Encoder/Decoder - Pure JavaScript Version
const fullText = msg.text || msg.caption || '';
const commandParts = fullText.split(' ');
const action = commandParts[1] ? commandParts[1].toLowerCase() : '';
const text = commandParts.slice(2).join(' ');

console.log('Debug:', { fullText, action, text });

if (!action) {
    const instructions = `üî§ *Base64 Converter*

*Usage:*
‚Ä¢ Encode: /base64 encode your_text
‚Ä¢ Decode: /base64 decode base64_string

*Examples:*
‚Ä¢ /base64 encode Hello World
‚Ä¢ /base64 decode SGVsbG8gV29ybGQ=`;

    return bot.sendMessage(chatId, instructions, { parse_mode: 'Markdown' });
}

if (!text) {
    return bot.sendMessage(chatId, '‚ùå Please provide text to encode/decode.\nExample: /base64 encode Hello World');
}

try {
    if (action === 'encode' || action === 'enc' || action === 'e') {
        // Encode to Base64 using JavaScript
        const encoded = Buffer.from(text).toString('base64');

        const response = `‚úÖ *Encoded to Base64*

*Original:* ${text}
*Base64:* \`\`\`${encoded}\`\`\`

*Quick Decode:* 
/base64 decode ${encoded}`;

        bot.sendMessage(chatId, response, { 
            parse_mode: 'Markdown',
            reply_to_message_id: msg.message_id 
        });

    } else if (action === 'decode' || action === 'dec' || action === 'd') {
        // Decode from Base64 using JavaScript
        try {
            const decoded = Buffer.from(text, 'base64').toString('utf-8');

            const response = `‚úÖ *Decoded from Base64*

*Base64:* ${text}
*Decoded:* \`\`\`${decoded}\`\`\``;

            bot.sendMessage(chatId, response, { 
                parse_mode: 'Markdown',
                reply_to_message_id: msg.message_id 
            });
        } catch (decodeError) {
            bot.sendMessage(chatId, `‚ùå *Decoding Failed*

Invalid Base64 string!

‚ö†Ô∏è Make sure the input is valid Base64 encoded text.`, { 
                parse_mode: 'Markdown',
                reply_to_message_id: msg.message_id 
            });
        }
    } else {
        bot.sendMessage(chatId, `‚ùå *Invalid Action: "${action}"*

Please use:
‚Ä¢ *encode* - Convert text to Base64
‚Ä¢ *decode* - Convert Base64 to text

*Examples:*
‚Ä¢ /base64 encode Hello World
‚Ä¢ /base64 decode SGVsbG8gV29ybGQ=`, { 
            parse_mode: 'Markdown',
            reply_to_message_id: msg.message_id 
        });
    }
} catch (error) {
    console.error('Base64 command error:', error);

    let errorMessage = '‚ùå *Conversion Error*\n\n';
    errorMessage += error.message;

    bot.sendMessage(chatId, errorMessage, { 
        parse_mode: 'Markdown',
        reply_to_message_id: msg.message_id 
    });
}
