answerHandler:
if (User.getData('calc_mode') === 'true') {
    const expression = params;

    try {
        // Basic safe evaluation
        const result = eval(expression);
        Api.sendMessage(`âœ… Calculation Result:\n\n\`${expression}\` = \`${result}\``);
    } catch (error) {
        Api.sendMessage(`âŒ Invalid math expression: \`${expression}\`\n\nPlease enter a valid expression.`);
    }

    User.saveData('calc_mode', 'false');
}

if (User.getData('waiting_range') === 'true') {
    const range = params;
    const match = range.match(/(\d+)-(\d+)/);

    if (match) {
        const min = parseInt(match[1]);
        const max = parseInt(match[2]);

        if (min >= max) {
            Api.sendMessage("âŒ Invalid range. First number should be smaller than second number.");
            return;
        }

        const randomNum = Math.floor(Math.random() * (max - min + 1)) + min;
        Api.sendMessage(`ğŸ² Random number between ${min} and ${max}:\n\n**${randomNum}**`);
    } else {
        Api.sendMessage("âŒ Invalid format. Please use: min-max (e.g., 1-100)");
    }

    User.saveData('waiting_range', 'false');
}
